import { Component, ViewChild } from '@angular/core';
import { Item } from '../../models/Item.model';
import { ItemService } from '../../services/item.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-new-shoes',
  standalone: true,
  imports: [FormsModule, CommonModule],
  templateUrl: './new-shoes.component.html'
})
export class NewShoesComponent {

    factory: Map<string, string> = new Map<string, string>;
    types: string[] = [];
    genres: Set<string> = new Set<string>;
    genresArray: string[] = [];
    newItem: Item | undefined;
    item = {
      name: '',
      description: '',
      price: 0,
      gender: '',
      type: ''
    }
  
    @ViewChild('file')
    file: any
  
    constructor(private router: Router, public itemService: ItemService){
      this.factory.set("jeans", "clothes");
      this.factory.set("camisa", "clothes");
      this.factory.set("camiseta", "clothes");
      this.factory.set("zapato", "shoes");
      this.types = Array.from(this.factory.keys()); 
  
      this.genres.add("man");
      this.genres.add("woman");
      this.genres.add("unisex");
      this.genresArray = Array.from(this.genres);
    }
  
    create(){
      this.itemService.addItem(this.item).subscribe(
        (item: any) => {
          this.newItem = item,
          this.addImage(),
          this.router.navigate(['/inventory/items/' + this.newItem?.id + '/shoes/stock'])
        },
        (error: any) => console.log(error)
      );
    }
  
    addImage(){
      const image = this.file.nativeElement.files[0];
      if(image && this.newItem != undefined){
        let formData = new FormData();
        formData.append("imageField", image);
        this.itemService.addImage(this.newItem, formData).subscribe(
          (_:any) => alert("image successfully updated"),
          (_:any) => alert("error updating item image")
        );
      }else{
        alert("create an item first")
      }
    }
  
    home(){
      this.router.navigate(['/inventory/items']);
    }
}
    <header>
        <nav class="navbar">
            <nav class="nav-menu --nav-dark" id="mySidenav">
                <a class="is-hidden-mobile brand is-uppercase has-text-weight-bold has-text-white" (click)="home()">Avenue Fashion</a>
                <ul class="nav-menu-ul"></ul>
            </nav>
        </nav>
    </header>
    <!-- Banner -->
    <div class="banner banner-second">
        <div class="banner-container">
        </div>
    </div>

    <div class="container">
        <div class="columns">
            <div class="column is-two-fifths-desktop">
                <div class="slider" id="slider">
                    <div>
                        <img src="https://pub-f65070d681344ae4af3e8b15f47129a8.r2.dev/no_image.png"  alt="camiseta" width="400" height="400">
                        <input #file type='file' name='itemImage' accept=".jpg, .jpeg, .png" required/>
                        <div class="columns is-multiline">
                            <div class="column is-full is-marginless">
                                <button (click)="addImage()" class="btn btn-default btn-outline">add image</button>
                            </div>
                        </div>
                    </div>
                </div>
                
            </div>
            <div class="column">
                <h3 class="is-size-3">New item</h3>
                <div class="columns">
                    <div class="column">
                        <input type="text" [(ngModel)]="item.name" placeholder="name" class="form-control-field" required/>
                    </div>
                    <div class="column">
                        <input type="number" [(ngModel)]="item.price" placeholder="price" class="form-control-field" min="0" step="0.01" required/>
                    </div>
                </div>

                <div class="columns is-multiline">
                    <div class="column is-one-third">
                        <label for="color">Gender:</label>
                        <div *ngFor="let genre of genresArray" class="form-control-field" id="gender">
                            <label><input type="radio" name="gender" [value]="genre" [(ngModel)]="item.gender"/>{{genre}}</label>
                        </div>
                    </div>
                    <div class="column is-one-third">
                        <label for="size">Type:</label>
                        <div *ngFor="let type of types" class="form-control-field" id="type">
                            <label><input type="radio" name="type" [value]="type" [(ngModel)]="item.type"/>{{type}}</label>
                        </div>
                    </div>
                </div>  
                
                <div class="columns is-multiline">
                    <div class="column is-full is-marginless">
                        <button (click)="create()" class="btn btn-default btn-outline">add stock</button>
                    </div>
                </div>

            </div>
        </div>
    </div>
    <div class="container">
        <div class="columns is-tablet">
            <div class="column">
                <div class="tabs-container">                
                    <div class="tabs-panels">
                        <div class="tab-panel active-panel">
                            <div class="container">
                                <div class="columns">
                                    <div class="column">
                                        <h2 class="is-size-4">Additional information</h2>
                                        <textarea [(ngModel)]="item.description" placeholder="description" class="form-control-field"></textarea>
                                    </div>
                                </div>
                            </div>
                        </div>                      
                    </div>
                </div>
            </div>
        </div>
    </div>

    <!-- footer -->
    <footer class="footer">
        <div class="footer-bar-top">
            <div class="container">
                <a class="footer-bar-top-links" href="#">2024 Avenue Fashion</a>
            </div>
        </div>
    </footer>
