import { Component } from '@angular/core';
import { Router, RouterModule, RouterOutlet } from "@angular/router";
import { UserService } from '../../services/user.service';
import { Item } from '../../models/Item.model';
import { ItemService } from '../../services/item.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [RouterModule],
  templateUrl: './login.component.html'
})
export class LoginComponent {

  //itemsFav: Item[] = [];
  //tam2: number = 10;
  //capacityFav: number = 0;

  constructor(private router: Router, private userService: UserService){}

  signin(event: any, user: string, pass: string) {

    event.preventDefault();

    this.userService.signin(user, pass);

    /*this.itemService.getUserFavItems(this.tam2, this.userService.getCurrentUser()).subscribe(
      (items: any) => {
          this.itemsFav = items.content;
          this.capacityFav = items.totalElements;
      },
      (error: any) => console.log(error)
    );*/

    this.router.navigate(['/home']);  
  }
  
  home(){
    this.router.navigate(['/home']);
  }
}
    <header>
        <nav class="navbar">
            <nav class="nav-menu --nav-dark-light" id="mySidenav">
                <button class="is-hidden-mobile brand is-uppercase has-text-weight-bold has-text-dark" (click)="home()">Avenue Fashion</button>
                <ul class="nav-menu-ul"></ul>
            </nav>
        </nav>
    </header>
    <!-- Banner -->
    <div class="banner banner-second">
        <div class="banner-container">
        </div>
    </div>

    <div class="container">
        <div class="columns">
            <div class="column">
                <h2 class="is-size-4">Login</h2>
                <input #user name="username" placeholder="Username" class="form-control-field error"/>

                <input #pass type="password" name="password" placeholder="Password" class="form-control-field"/>
                <div class="title-hd-sm">
                    <button class="btn btn-default btn-primary" (click)="signin($event, user.value, pass.value)" type="submit" value="Log in">Sign in</button>
                </div>
            </div>
        </div>
    </div>

    <div id="spinner-div" class="pt-5">
        <div class="spinner-border text-primary" role="status">
        </div>
    </div>

    <!-- footer -->
    <footer class="footer">
        <div class="footer-bar-top">
            <li class="footer-bar-top-links">2024 Avenue Fashion</li>
        </div>
    </footer>
