import { Component, ViewChild, viewChild } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '../../services/user.service';
import { User } from '../../models/User.model';
import { FormsModule } from '@angular/forms';
import { Direction } from '../../models/Direction.model';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-signup',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './signup.component.html',
  styleUrl: './signup.component.css'
})
export class SignupComponent {

  id: number = 0;
  user = {
    username: '',
    name: '',
    email: '',
    password: '',
    passwordConfirmation: '',
    rol: 'USER',
    code: '',
    street: '',
    number: 0,
    zipCode: 0,
    city: ''
  }

  @ViewChild("file")
  file: any;

  constructor(private router: Router, public userService: UserService){}

  ngOnInit(){
    if(this.userService.isLogged() && this.userService.isUser())
      this.id = this.userService.getCurrentUser().id;
  }

  home(){
    this.router.navigate(['/home']);
  }

  userImage(id: number | undefined){
    return '/store/api/users/' + id + '/image';
  }

  anonymousImage(){
    return '/store/api/users/6/image';
  }

  register() {
    this.userService.addUser(this.user).subscribe(
      (user: any) => { 
        console.log(user)
        this.uploadImage(user)
      },
      (_: any) => alert('failed to register')
    );  
  }

  uploadImage(user: User): void {
    const image = this.file.nativeElement.files[0];
    if(image) {
      let formData = new FormData();
      formData.append("imageFile",image);
      this.userService.setUserImage(user,formData).subscribe(
        (_: any) => { 
          alert('registered successfully')
        },
        (_: any) => alert('error uploading user image')
      );
    }
  }
}
    <header>
        <nav class="navbar">
            <nav class="nav-menu --nav-dark-light" id="mySidenav">
                <button class="is-hidden-mobile brand is-uppercase has-text-weight-bold has-text-dark" (click)="home()">Avenue Fashion</button>
                <ul class="nav-menu-ul"></ul>
            </nav>
        </nav>
    </header>
    <!-- Banner -->
    <div class="banner banner-second">
        <div class="banner-container ">
            <div *ngIf="this.userService.isLogged()">
                <div class="user-img">
                    <img [src]="userImage(id)" alt="" width="200" height="200"/>
                </div>
            </div>
            <div *ngIf="!this.userService.isLogged()">
                <div class="user-img">
                    <img [src]="anonymousImage()" alt="" width="200" height="200"/>
                </div>
            </div>
        </div>
    </div>

    <div class="container">
        <div class="columns">
            <div class="column">
                <h2 class="is-size-4">Registration</h2>
                <input type="text" [(ngModel)]="user.username" placeholder="User name" class="form-control-field" required/>
                <input type="text" [(ngModel)]="user.name" placeholder="Name" class="form-control-field" required/>
                <input type="email" [(ngModel)]="user.email" placeholder="Email" class="form-control-field" required/>
                <input type="password" [(ngModel)]="user.password" placeholder="Password" class="form-control-field" minlength="5" maxlength="10" required/>
                <input type="password" [(ngModel)]="user.passwordConfirmation" placeholder="Confirm your password" class="form-control-field" minlength="5" maxlength="10" required/>
                <div class="user-img">
                    <input #file type='file' name='imageFile' accept=".jpg, .jpeg" required/>
                </div> 
                <h2 class="is-size-4">Address</h2>
                <div class="columns">
                    <div class="column">
                        <input type="text" [(ngModel)]="user.street" placeholder="Street" class="form-control-field" required/>
                        <input type="number" [(ngModel)]="user.number" placeholder="Number" class="form-control-field" required/>
                    </div>
                    <div class="column">
                        <input type="number" [(ngModel)]="user.zipCode" placeholder="00000" class="form-control-field" minlength="5" maxlength="5" required/>
                        <input type="text" [(ngModel)]="user.city" placeholder="City" class="form-control-field" required/>
                    </div>
                </div>
                <button (click)="register()" class="btn btn-default btn-primary">Create account</button>
            </div>
        </div>
    </div>

    <div id="spinner-div" class="pt-5">
        <div class="spinner-border text-primary" role="status">
        </div>
    </div>

    <!-- footer -->
    <footer class="footer">
        <div class="footer-bar-top">
            <li class="footer-bar-top-links">2024 Avenue Fashion</li>
        </div>
    </footer>
